package arrays;

public class OneDimensionalArrays {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int array[] = new int[10];
		array[0] = 10;
		array[5] = 20;
		array[9] = 30;
		
		for (int i = 0; i <= array.length; i++) {
			System.out.println(array[i]);
		}

	}

}
